mapper.select:
SELECT id, name, start_path, end_path, type, state, java_home
FROM chaos_shell
WHERE is_deleted = 0;

SELECT id, name, start_path, end_path, type, state, java_home
FROM chaos_shell
WHERE id = #{id} and is_deleted = 0;

mapper.insert:
INSERT INTO chaos_shell
    (name, start_path, end_path, type, state, java_home)
VALUES (#{name}, #{startPath}, #{endPath}, #{type}, #{state}, #{javaHome});
